# Les différentes méthodes

def total_handles(handles)
 puts "Le nombre total de journalistes est : #{handles.size}"
end


def handles_with_number(handles)
 count = handles.count do |handle|
 handle.match?(/\d/) # \d : signifie chiffre
 end
 puts "Le nombre de handles contenant un chiffre est : #{count}"
end


def handles_with_aude(handles)
 count = 0

handles.each do |handle|
  if handle.match?(/aude/i)
    count += 1
  end
end
 puts "Le nombre de handle contenant le prénom aude est : #{count}"
end


def handles_start_with_capital(handles)
 count = 0

  handles.each do |handle|
    first_letter = handle[1]
    if first_letter == first_letter.upcase && first_letter != first_letter.downcase
      count += 1
    end
  end
 puts "Le nombre de handle qui commence par une majuscule après le @ est : #{count}"
end


def handles_with_any_capital(handles)
count = 0

  handles.each do |handle|
    handle.each_char do |char|
      if char == char.upcase && char != char.downcase
        count += 1
        break 
      end
    end
  end
 puts "Le nombre de handle contenant au moins une majuscule : #{count}"
end


def total_underscores(handles)
 total = 0
  handles.each do |handle|
    total += handle.count("_")
  end
 puts "Le nombre total de underscore (_) est : #{total}"
end


def sorted_handles(handles)
  sorted = handles.sort_by { |h| h.downcase }
  puts "La liste des handle par ordre alphabétique est : "
  puts sorted
end


def shortest_50_handles(handles)
  sorted = handles.sort_by { |h| h.length }
 puts "Les 50 handle les plus courts sont : "
 puts sorted.first(50)
end


def position_of_epenser(handles)
 index = handles.index("@epenser")
  
  if index
    puts "@epenser est à la #{index + 1}e position dans l'array."
  else
    puts "@epenser n'est pas présent dans la liste."
  end
end

# Le menu

loop do
  puts "\n    Que voulez vous savoir ? :"
  puts "1.Le nombre total de journalistes."
  puts "2.Le nombre de handle contenant un numéro."
  puts "3.Le nombre de handle contenant le prénom aude."
  puts "4.Le nombre de handle qui commence par une majuscule après le @."
  puts "5.Le nombre de handle contenant au moins une majuscule."
  puts "6.Le nombre total de underscore (_)."
  puts "7.La liste des handle par ordre alphabétique."
  puts "8.Les 50 handle les plus courts."
  puts "9.La position de @epenser."
  puts "10.Quitter"
  print ">"
  
  choix = gets.chomp

  case choix
  when "1"
    total_handles(handles)
  when "2"
    handles_with_number(handles)
  when "3"
    handles_with_aude(handles)
  when "4"
    handles_start_with_capital(handles)
  when "5"
    handles_with_any_capital(handles)
  when "6"
    total_underscores(handles)
  when "7"
    sorted_handles(handles)
  when "8"
    shortest_50_handles(handles)
  when "9"
    position_of_epenser(handles) 
  when "10" 
    puts "Au revoir !"
    break
  else
    puts "\nChoix invalide."
  end
end
